import React, {useState } from "react";
import { UilSearch, UilLocationPoint } from "@iconscout/react-unicons";
import { getFormattedData } from '../makeitwork/weather';

function Searchbar({ onToggleTemperatureUnit, onCitySelect,onFetchHourlyData})  {

    const [searchQuery, setSearchQuery] = useState("");
    const [temperatureUnit, setTemperatureUnit] = useState('metric');
    const [currentLocation, setCurrentLocation] = useState(null);

    async function fetchHourlyData(latitude, longitude, date) {
        const apiUrl = `https://api.brightsky.dev/weather?lat=${latitude}&lon=${longitude}&date=${date}`;
        const response = await fetch(apiUrl);
        const data = await response.json();
        return data.weather;
    }

    const handleLocationClick = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
              async (position) => {
                const { latitude, longitude } = position.coords;
                console.log("User's location:", latitude, longitude);
                try {
                  const data = await getFormattedData({ lat: latitude, lon: longitude });
                  onCitySelect(data.currentFormattedData);
                  setSearchQuery("");
                } catch (error) {
                  console.error("Error getting weather data:", error.message);
                }
              },
            (error) => {
              console.error("Error getting user's location:", error.message);
            }
          );
        } else {
            console.error("Geolocation is not supported by this browser.");
          }
    };

    const handleInputClick = (e) => {
        e.target.select();
    };

    const handleSearchClick = async () => {
        if (searchQuery.trim() !== "") {
            console.log("Searching for:", searchQuery);
            onCitySelect(searchQuery, temperatureUnit);            setSearchQuery("");
            setCurrentLocation(null);
        }
        };

    const handleInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    return (
        <div className="flex flex-row justify-center my-6">
            <div className="flex flex-row w-3/4 items-center justify-center space-x-4">
                <input
                type="text"
                value={searchQuery}
                onChange={handleInputChange}
                onClick={handleInputClick}
                onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                        handleSearchClick();
                    }
                }}
                placeholder=" Search for city.... "
                className="text-xl font-light p-2 w-full shadow-xl focus:outline-none capitalize placeholder:lowercase rounded-full"
                />
                <UilSearch
                    size={30}
                    onClick={handleSearchClick}
                    className="text-white cursor-pointer transition ease-out hover:scale-125"
                />
                <UilLocationPoint
                    size={30}
                    onClick={handleLocationClick}
                    className="text-white cursor-pointer transition ease-out hover:scale-125"
                />
            </div>

          <div className="flex flex-row w-1/4 items-center justify-center">
            <button
              name="metric"
              onClick={() => onToggleTemperatureUnit('metric')}
              className="text-xl text-white font-light transition ease-out hover:scale-125"
            >
              Â°C
            </button>
            <p className="text-xl text-white mx-1">|</p>
            <button
              name="imperial"
              onClick={() => onToggleTemperatureUnit('imperial')}
              className="text-xl text-white font-light transition ease-out hover:scale-125"
            >
              Â°F
            </button>
          </div>
        </div>
      );
}

export default Searchbar
