        const fetchData = async () => {
          try {
            if (currentFormattedData) {
              const details = await br(currentFormattedData);
              setWeatherDetails(details);
            }
          } catch (error) {
            console.error('Error fetching hourly forecast:', error);
            setHasError(true);
