import './App.css';
import Temperature from './components/Temperature';
import Details from './components/Details';
import getFormattedData from './makeitwork/weather';
import Searchbar from './components/Searchbar';
import { useState , useEffect} from 'react';


function App() {

  const [query, setQuery] = useState({ q: "berlin" });
  const [units, setUnits] = useState("metric");
  const [weather, setWeather] = useState(null);

  const formatBackground = () => {
    if (!weather) return "from-cyan-700 to-blue-700";
    const threshold = units === "metric" ? 20 : 60;
    if (weather.temp <= threshold) return "from-cyan-700 to-blue-700";

    return "from-yellow-700 to-orange-700";
  };

  useEffect(() => {
    const fetchWeather = async () => { await getFormattedData ({ ...query, units }).then((data) => {
       setWeather(data);
      });
    };

    fetchWeather();
  }, [query, units]);


  return (
    <div className={`mx-auto max-w-screen-md mt-4 py-5 px-32 bg-gradient-to-br  h-fit shadow-xl shadow-gray-400 ${formatBackground()}`}> 
     <Searchbar setQuery={setQuery} units={units} setUnits={setUnits} />
     {weather && (
        <Temperature weather={weather} />
      )}
      <Details h="hourly forecast" />
      <Details h="daily forecast" />
   </div>
  );
}

export default App;
