      const fetchHourlyForecast = async () => {
        try {
          const hourlyData = currentFormattedData.data.forecast.forecastday[0].hour;
          setWeatherDetails(hourlyData);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching hourly forecast data:', error);
        }
      };
      fetchHourlyForecast();
    }, [currentFormattedData]); 
    return (
      <div>
        <div className="flex items-center justify-stat mt-6">
          <p className="text-white font-medium uppercase">{h}</p>
        </div>
        <hr className="my-2"></hr>
