          {currentFormattedData.data.forecast.forecastday[0].hour.map((weatherItem, index) => (
            <div
              key={index}
              className="flex flex-col items-center justify-center mx-4"
              style={{ backgroundColor: 'rgb(216, 216, 216, 0.2)', borderRadius: '8px' }}
            >
              <p className="font-light text-sm">
                {new Date(weatherItem.time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </p>
              <Icon icon={`meteocons:${weatherItem.condition.icon}-fill`} width="45" className="my-1" />
              <p className="font-medium">{round(weatherItem.temp_c)}Â°</p>
            </div>
          ))}
        </div>
      </div>
    );
  }

