import './App.css';
import Temperature from './components/Temperature';
import Details from './components/Details';
import getFormattedData from './makeitwork/weather';
import Searchbar from './components/Searchbar';


function App() {
  const [weatherData, setWeatherData] = useState(null);

  const fetchWeather = async () => {
      const data = await getFormattedData({ q: "berlin" });
      setWeatherData(data.currentFormattedData);
  }

  useEffect(() => {
      fetchWeather();
  }, [])

  return (
   <div className={`mx-auto max-w-screen-md mt-4 py-5 px-32 bg-gradient-to-br  from-cyan-500 to to-blue-700 `}>
      <Searchbar/>
      <Temperature />
      <Details h="hourly forecast" />
      <Details h="daily forecast" />
   </div>
  );
}

export default App;
