import React, {useState } from "react";
import { UilSearch, UilLocationPoint , UilMap} from "@iconscout/react-unicons";
import { getFormattedData } from '../makeitwork/weather';


function Searchbar({ onToggleTemperatureUnit, onCitySelect})  {

    const [searchQuery, setSearchQuery] = useState("");
    const [temperatureUnit, setTemperatureUnit] = useState('metric');
    const [currentLocation, setCurrentLocation] = useState(null);
    const [citySuggestions, setCitySuggestions] = useState([]);
    const handleLocationClick = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
              async (position) => {
                const latitude = position.coords.latitude;
                const longitude = position.coords.longitude;
                try {
                  const reverseGeocodeUrl = `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`;
                  const reverseGeocodeResponse = await fetch(reverseGeocodeUrl);
                  const reverseGeocodeData = await reverseGeocodeResponse.json();
                  const city = reverseGeocodeData.city;
                  console.log("City:", city);
                  onCitySelect(city,temperatureUnit);
                } catch (error) {
                  console.error("Error getting weather data:", error.message);
                }
              },
            (error) => {
              console.error("Error getting user's location:", error.message);
            }
          );
        } else {
            console.error("Geolocation is not supported by this browser.");
          }
    };

    const handleInputClick = (e) => {
        e.target.select();
    };

    const handleInputChange = (e) => {
      const query = e.target.value.trim();

      if (query === "") {
        setCitySuggestions([]);
        setSearchQuery("");
        return;
      }

      try {
        const countrySuggestionUrl = `https://restcountries.com/v3.1/name/${query}`;
        fetch(countrySuggestionUrl)
          .then((countryResponse) => {
            if (!countryResponse.ok) {
              throw new Error(`Request failed with status: ${countryResponse.status}`);
            }
            return countryResponse.json();
          })
          .then((countryData) => {
            if (Array.isArray(countryData)) {
              const filteredSuggestions = countryData
                .filter((country) => country.name.common.toLowerCase().includes(query.toLowerCase()))
                .slice(0, 10);
              setCitySuggestions(filteredSuggestions);
            } else {
              console.error("Country suggestions data is not in the expected format:", countryData);
            }
          })
          .catch((error) => {
            console.error("Error getting country suggestions:", error.message);
          });
      } catch (error) {
        console.error("Error getting country suggestions:", error.message);
      }

      setSearchQuery(query);
    };

    return (
        <div className="flex flex-row justify-center my-6 relative">
            <div className="flex flex-row w-3/4 items-center justify-center space-x-4 relative">
                <input
                type="text"
                value={searchQuery}
                onChange={handleInputChange}
                onClick={handleInputClick}
                onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                        handleSearchClick();
                    }
                }}
                placeholder=" Search for city.... "
                className="text-xl font-light p-2 w-full shadow-xl focus:outline-none capitalize placeholder:lowercase rounded-full"
                />
                <UilSearch
                    size={30}
                    onClick={handleSearchClick}
                    className="text-white cursor-pointer transition ease-out hover:scale-125"
                />
                  {citySuggestions.length > 0 && (
                    <ul className="absolute top-14 p-4 bg-slate-800 w-full rounded-xl left-1/2 -translate-x-1/2 flex flex-col gap-2 max-h-[10.5rem] lg:max-h-[21rem] overflow-y-auto">
                      {citySuggestions.map((suggestion) => (
                        <div key={suggestion.name.common} className="border-b-[1px] border-b-slate-700 py-1 w-[99%]">
                          <div className="flex gap-2 items-center hover:bg-slate-700 duration-500 cursor-pointer">
                            <img
                              title={suggestion.name.common}
                              src={suggestion.flags[0]}
                              style={{ display: "inline-block", width: "1.5rem", height: "1em", verticalAlign: "middle" }}
                              alt={suggestion.name.common}
                            />
                            <div>{suggestion.name.common}</div>
                          </div>
                        </div>
                      ))}
                    </ul>
                  )}
                  <UilLocationPoint
                    size={30}
                    onClick={handleLocationClick}
                    className="text-white cursor-pointer transition ease-out hover:scale-125"
                />
                <UilMap
                size={30}
                className="text-white cursor-pointer transition ease-out hover:scale-125"
                />


            </div>

          <div className="flex flex-row w-1/4 items-center justify-center">
            <button
              name="metric"
              onClick={() => onToggleTemperatureUnit('metric')}
              className="text-xl text-white font-light transition ease-out hover:scale-125"
            >
              °C
            </button>
            <p className="text-xl text-white mx-1">|</p>
            <button
              name="imperial"
              onClick={() => onToggleTemperatureUnit('imperial')}
              className="text-xl text-white font-light transition ease-out hover:scale-125"
            >
              °F
            </button>
          </div>
        </div>
      );
}

export default Searchbar
