import { DateTime } from "luxon";

const API_KEY ="558b6b9ac644331a1081da7d76b84230";
const BASE_URL ="https://api.openweathermap.org/data/2.5";


const getData = (infotype, searchParams) => {
    const url = new URL(BASE_URL + "/" + infotype);
    url.search = new URLSearchParams({ ...searchParams, appid: API_KEY})
    return fetch(url).then((res) => res.json())
    console.log(res);
};

const formatCurrentData = (data) => { 
    const{ 
        main: {temp, feels_like, temp_min, temp_max, humidity},
        sys:{ country, sunrise, sunset},
        coord: {lat, lon},
        wind:{speed},
        weather,
        name,
        dt,    
    } = data ;
    const { main: details, icon } = weather[0];
    return {
        lat,
        lon,
        temp,
        feels_like,
        temp_min,
        temp_max,
        humidity,
        name,
        dt,
        country,
        sunrise,
        sunset,
        details,
        icon,
        speed,
    };
};

const formatData = (data) => {
    let {eday, ehour, timezone} = data ;
    console.log("Data received:", data);

    eday = eday.slice(1, 6).map((d) => { 
        return {
            titel: formatToLT(d.dt, timezone, "ccc"),
            temp: d.temp.day,
            icon: d.weather[0].icon
        };
    });
    ehour = ehour.slice(1, 6).map((d) => { 
        return {
            titel: formatToLT(d.dt, timezone, "hh:mm a"),
            temp: d.temp,
            icon: d.weather[0].icon,
        };
    });

    return { eday, ehour, timezone};
};

const getFormattedData = async (searchParams) => { 
    const currentFormattedData = await getData
    ("weather", searchParams).then(formatCurrentData);

    const {lat, lon} = currentFormattedData;

    const forecastData = await getData("onecall", {
        lat, lon, exclude:"current,minutely,alerts", 
        units: searchParams.units,
    }).then(formatData);

    return { ...currentFormattedData, ...forecastData };
};


const formatToLT = (secs, zone, 
    format = "cccc, dd LLL yyyy' | Local time: ' hh:mm a"
) => DateTime.fromSeconds(secs).setZone(zone).toFormat(format)



export default getFormattedData;
