import { DateTime } from "luxon";

const API_KEY ="558b6b9ac644331a1081da7d76b84230";
const BASE_URL ="https://api.openweathermap.org/data/2.5";

const IconURL = (iconId) => `http://openweathermap.org/img/wn/${iconId}@2x.png`;

const getData = (infotype, searchParams) => {
    const url = new URL(BASE_URL + "/" + infotype);
    url.search = new URLSearchParams({ ...searchParams, appid: API_KEY})
    return fetch(url).then((res) => res.json())
};

const formatCurrentData = (data) => { 
    const{ 
        main: {temp, feels_like, temp_min, temp_max, humidity},
        sys:{ country, sunrise, sunset},
        coord: {lat, lon},
        wind:{speed},
        timezone,
        weather,
        name,
        dt,    
    } = data ;
    if (
        temp === undefined ||
        feels_like === undefined ||
        temp_min === undefined ||
        temp_max === undefined ||
        humidity === undefined ||
        country === undefined ||
        sunrise === undefined ||
        sunset === undefined ||
        lat === undefined ||
        lon === undefined ||
        speed === undefined ||
        timezone === undefined ||
        weather === undefined ||
        name === undefined ||
        dt === undefined
    ) {
        throw new Error("Missing or undefined properties in weather data.");
    }
    const { description, icon } = weather[0];
    return {
        description,
        lat,
        lon,
        temp,
        feels_like,
        temp_min,
        temp_max,
        humidity,
        name,
        dt,
        country,
        sunrise,
        sunset,
        iconurl : IconURL(icon),
        speed,
        timezone,
    };
};


const getFormattedData = async (searchParams) => { 
    const { q } = searchParams;
    const isCoordinates = q && q.match(/^(-?\d+(\.\d+)?),\s*(-?\d+(\.\d+)?)$/);
    try {
        let data;
        if (isCoordinates) {
            data = await getData("weather", { ...searchParams, q: undefined });
        } else {
            data = await getData("weather", searchParams);
        }

        const currentFormattedData = formatCurrentData(data);
        console.log(currentFormattedData);
        return { currentFormattedData };
    } catch (error) {
        console.error("Error fetching weather data:", error.message);
        throw error; 
    }
};

const formatTLT = (
    secs,
    zone,
    format = "hh:mm a"
) => DateTime.fromSeconds(secs).setZone(zone).toFormat(format);

const CustomDate = (secs, timezone, format = "cccc, dd LLL yyyy' | Local time: 'hh:mm a") => {
    const dt = DateTime.fromSeconds(secs).plus({ seconds: timezone });
    const formattedDate = dt.toFormat(format);
    return formattedDate;
};

const round = (number) => {
    return number !== undefined ? number.toFixed(0) : '';
  };
export default getFormattedData;
export { formatTLT, CustomDate, round}
